//# Get revision info for package BBSKernel; generated by CMake

#include <BBSKernel/Package__Version.h>
#include <Common/Version.h>
#include <Blob/Package__Version.h>
#include <Common/Package__Version.h>
#include <ParmDB/Package__Version.h>
#include <StationResponse/Package__Version.h>

namespace LOFAR {
  Version BBSKernelVersion::getInfo()
  {
    return Version ("BBSKernel",
                    getVersion(), getConfVersion(),
                    getRevision(), getPackageRevision(),
                    getNrChangedFiles(),
                    getBuildTime(), getBuildUser(), getBuildMachine());
  }

  std::string BBSKernelVersion::getVersion()
    { return "CEP-Imager-Task5206"; }
  std::string BBSKernelVersion::getConfVersion()
    { return "1.0"; }
  std::string BBSKernelVersion::getRevision()
    { return "31269"; }
  std::string BBSKernelVersion::getPackageRevision()
    { return "29845"; }
  std::string BBSKernelVersion::getNrChangedFiles()
    { return "0"; }
  std::string BBSKernelVersion::getBuildTime()
  { return std::string(__DATE__) + " " + std::string(__TIME__); }
  std::string BBSKernelVersion::getBuildUser()
  { return "iniyan"; }
  std::string BBSKernelVersion::getBuildMachine()
  { return "pyimager.c.meerkat-7-gazing.internal"; }
  void BBSKernelVersion::fillInfo
    (int level, std::vector<Version::PkgInfo>& vec)
  {
    // Add info of this package if needed.
    vec.push_back (Version::PkgInfo(level, &getInfo));
    // Add info of packages being used.
    BlobVersion::fillInfo (level+1, vec);
    CommonVersion::fillInfo (level+1, vec);
    ParmDBVersion::fillInfo (level+1, vec);
    StationResponseVersion::fillInfo (level+1, vec);
  }
}
