//# Print revision info for package BBSKernel; generated by CMake

#include <BBSKernel/Package__Version.h>
#include <Common/Version.h>
#include <iostream>

using namespace LOFAR;

int main (int argc, const char* argv[])
{
  std::string type = "brief";
  if (argc > 1) {
    type = argv[1];
  }
  Version::show<BBSKernelVersion> (std::cout, "BBSKernel", type);
  return 0;
}
