//# Get revision info for package LMWCommon; generated by CMake

#include <LMWCommon/Package__Version.h>
#include <Common/Version.h>
#include <Common/Package__Version.h>
#include <Blob/Package__Version.h>

namespace LOFAR {
  Version LMWCommonVersion::getInfo()
  {
    return Version ("LMWCommon",
                    getVersion(), getConfVersion(),
                    getRevision(), getPackageRevision(),
                    getNrChangedFiles(),
                    getBuildTime(), getBuildUser(), getBuildMachine());
  }

  std::string LMWCommonVersion::getVersion()
    { return "CEP-Imager-Task5206"; }
  std::string LMWCommonVersion::getConfVersion()
    { return "0.1"; }
  std::string LMWCommonVersion::getRevision()
    { return "31269"; }
  std::string LMWCommonVersion::getPackageRevision()
    { return "27477"; }
  std::string LMWCommonVersion::getNrChangedFiles()
    { return "0"; }
  std::string LMWCommonVersion::getBuildTime()
  { return std::string(__DATE__) + " " + std::string(__TIME__); }
  std::string LMWCommonVersion::getBuildUser()
  { return "iniyan"; }
  std::string LMWCommonVersion::getBuildMachine()
  { return "pyimager.c.meerkat-7-gazing.internal"; }
  void LMWCommonVersion::fillInfo
    (int level, std::vector<Version::PkgInfo>& vec)
  {
    // Add info of this package if needed.
    vec.push_back (Version::PkgInfo(level, &getInfo));
    // Add info of packages being used.
    CommonVersion::fillInfo (level+1, vec);
    BlobVersion::fillInfo (level+1, vec);
  }
}
