//# Get revision info for package ParmDB; generated by CMake

#include <ParmDB/Package__Version.h>
#include <Common/Version.h>
#include <Common/Package__Version.h>
#include <Blob/Package__Version.h>
#include <LMWCommon/Package__Version.h>

namespace LOFAR {
  Version ParmDBVersion::getInfo()
  {
    return Version ("ParmDB",
                    getVersion(), getConfVersion(),
                    getRevision(), getPackageRevision(),
                    getNrChangedFiles(),
                    getBuildTime(), getBuildUser(), getBuildMachine());
  }

  std::string ParmDBVersion::getVersion()
    { return "CEP-Imager-Task5206"; }
  std::string ParmDBVersion::getConfVersion()
    { return "1.0"; }
  std::string ParmDBVersion::getRevision()
    { return "31269"; }
  std::string ParmDBVersion::getPackageRevision()
    { return "29385"; }
  std::string ParmDBVersion::getNrChangedFiles()
    { return "0"; }
  std::string ParmDBVersion::getBuildTime()
  { return std::string(__DATE__) + " " + std::string(__TIME__); }
  std::string ParmDBVersion::getBuildUser()
  { return "iniyan"; }
  std::string ParmDBVersion::getBuildMachine()
  { return "pyimager.c.meerkat-7-gazing.internal"; }
  void ParmDBVersion::fillInfo
    (int level, std::vector<Version::PkgInfo>& vec)
  {
    // Add info of this package if needed.
    vec.push_back (Version::PkgInfo(level, &getInfo));
    // Add info of packages being used.
    CommonVersion::fillInfo (level+1, vec);
    BlobVersion::fillInfo (level+1, vec);
    LMWCommonVersion::fillInfo (level+1, vec);
  }
}
