//# Print revision info for package ParmDB; generated by CMake

#include <ParmDB/Package__Version.h>
#include <Common/Version.h>
#include <iostream>

using namespace LOFAR;

int main (int argc, const char* argv[])
{
  std::string type = "brief";
  if (argc > 1) {
    type = argv[1];
  }
  Version::show<ParmDBVersion> (std::cout, "ParmDB", type);
  return 0;
}
