//# Get revision info for package Blob; generated by CMake

#include <Blob/Package__Version.h>
#include <Common/Version.h>
#include <Common/Package__Version.h>

namespace LOFAR {
  Version BlobVersion::getInfo()
  {
    return Version ("Blob",
                    getVersion(), getConfVersion(),
                    getRevision(), getPackageRevision(),
                    getNrChangedFiles(),
                    getBuildTime(), getBuildUser(), getBuildMachine());
  }

  std::string BlobVersion::getVersion()
    { return "CEP-Imager-Task5206"; }
  std::string BlobVersion::getConfVersion()
    { return "1.2"; }
  std::string BlobVersion::getRevision()
    { return "31269"; }
  std::string BlobVersion::getPackageRevision()
    { return "27477"; }
  std::string BlobVersion::getNrChangedFiles()
    { return "0"; }
  std::string BlobVersion::getBuildTime()
  { return std::string(__DATE__) + " " + std::string(__TIME__); }
  std::string BlobVersion::getBuildUser()
  { return "iniyan"; }
  std::string BlobVersion::getBuildMachine()
  { return "pyimager.c.meerkat-7-gazing.internal"; }
  void BlobVersion::fillInfo
    (int level, std::vector<Version::PkgInfo>& vec)
  {
    // Add info of this package if needed.
    vec.push_back (Version::PkgInfo(level, &getInfo));
    // Add info of packages being used.
    CommonVersion::fillInfo (level+1, vec);
  }
}
