//# Print revision info for package Blob; generated by CMake

#include <Blob/Package__Version.h>
#include <Common/Version.h>
#include <iostream>

using namespace LOFAR;

int main (int argc, const char* argv[])
{
  std::string type = "brief";
  if (argc > 1) {
    type = argv[1];
  }
  Version::show<BlobVersion> (std::cout, "Blob", type);
  return 0;
}
