//# Get revision info for package Common; generated by CMake

#include <Common/Package__Version.h>
#include <Common/Version.h>

namespace LOFAR {
  Version CommonVersion::getInfo()
  {
    return Version ("Common",
                    getVersion(), getConfVersion(),
                    getRevision(), getPackageRevision(),
                    getNrChangedFiles(),
                    getBuildTime(), getBuildUser(), getBuildMachine());
  }

  std::string CommonVersion::getVersion()
    { return "CEP-Imager-Task5206"; }
  std::string CommonVersion::getConfVersion()
    { return "3.3"; }
  std::string CommonVersion::getRevision()
    { return "31269"; }
  std::string CommonVersion::getPackageRevision()
    { return "29121"; }
  std::string CommonVersion::getNrChangedFiles()
    { return "0"; }
  std::string CommonVersion::getBuildTime()
  { return std::string(__DATE__) + " " + std::string(__TIME__); }
  std::string CommonVersion::getBuildUser()
  { return "iniyan"; }
  std::string CommonVersion::getBuildMachine()
  { return "pyimager.c.meerkat-7-gazing.internal"; }
  void CommonVersion::fillInfo
    (int level, std::vector<Version::PkgInfo>& vec)
  {
    // Add info of this package if needed.
    vec.push_back (Version::PkgInfo(level, &getInfo));
    // Add info of packages being used.
  }
}
