//# Print revision info for package pytools; generated by CMake

#include <pytools/Package__Version.h>
#include <Common/Version.h>
#include <iostream>

using namespace LOFAR;

int main (int argc, const char* argv[])
{
  std::string type = "brief";
  if (argc > 1) {
    type = argv[1];
  }
  Version::show<pytoolsVersion> (std::cout, "pytools", type);
  return 0;
}
